void findCoefficientsForL2Poly(double * L2, double ** Q, double L1) {
L2[0]=Q[4][0]*Q[6][4]-Q[4][4]*Q[6][0]+L_1*Q[4][0]*Q[6][5]-L_1*Q[4][5]*Q[6][0]-L_1*Q[4][0]*Q[7][4]+L_1*Q[4][4]*Q[7][0]-L_1*Q[5][0]*Q[6][4]+L_1*Q[5][4]*pow(Q[6][0]-L_1, 2)*Q[4][0]*pow(Q[7][5]+L_1, 2)*Q[4][5]*pow(Q[7][0]-L_1, 2)*Q[5][0]*pow(Q[6][5]+L_1, 2)*Q[5][5]*pow(Q[6][0]+L_1, 2)*Q[5][0]*pow(Q[7][4]-L_1, 2)*Q[5][4]*pow(Q[7][0]+L_1, 3)*Q[5][0]*pow(Q[7][5]-L_1, 3)*Q[5][5]*Q[7][0];
L2[1]=Q[4][0]*Q[6][2]-Q[4][2]*Q[6][0]+Q[4][1]*Q[6][4]-Q[4][4]*Q[6][1]+L_1*Q[4][0]*Q[6][3]-L_1*Q[4][3]*Q[6][0]+L_1*Q[4][1]*Q[6][5]-L_1*Q[4][5]*Q[6][1]-L_1*Q[4][0]*Q[7][2]+L_1*Q[4][2]*Q[7][0]-L_1*Q[5][0]*Q[6][2]+L_1*Q[5][2]*Q[6][0]-L_1*Q[4][1]*Q[7][4]+L_1*Q[4][4]*Q[7][1]-L_1*Q[5][1]*Q[6][4]+L_1*Q[5][4]*pow(Q[6][1]-L_1, 2)*Q[4][0]*pow(Q[7][3]+L_1, 2)*Q[4][3]*pow(Q[7][0]-L_1, 2)*Q[5][0]*pow(Q[6][3]+L_1, 2)*Q[5][3]*pow(Q[6][0]-L_1, 2)*Q[4][1]*pow(Q[7][5]+L_1, 2)*Q[4][5]*pow(Q[7][1]-L_1, 2)*Q[5][1]*pow(Q[6][5]+L_1, 2)*Q[5][5]*pow(Q[6][1]+L_1, 2)*Q[5][0]*pow(Q[7][2]-L_1, 2)*Q[5][2]*pow(Q[7][0]+L_1, 3)*Q[5][0]*pow(Q[7][3]-L_1, 3)*Q[5][3]*pow(Q[7][0]+L_1, 2)*Q[5][1]*pow(Q[7][4]-L_1, 2)*Q[5][4]*pow(Q[7][1]+L_1, 3)*Q[5][1]*pow(Q[7][5]-L_1, 3)*Q[5][5]*Q[7][1];
L2[2]=Q[4][1]*Q[6][2]-Q[4][2]*Q[6][1]+L_1*Q[4][1]*Q[6][3]-L_1*Q[4][3]*Q[6][1]-L_1*Q[4][1]*Q[7][2]+L_1*Q[4][2]*Q[7][1]-L_1*Q[5][1]*Q[6][2]+L_1*Q[5][2]*pow(Q[6][1]-L_1, 2)*Q[4][1]*pow(Q[7][3]+L_1, 2)*Q[4][3]*pow(Q[7][1]-L_1, 2)*Q[5][1]*pow(Q[6][3]+L_1, 2)*Q[5][3]*pow(Q[6][1]+L_1, 2)*Q[5][1]*pow(Q[7][2]-L_1, 2)*Q[5][2]*pow(Q[7][1]+L_1, 3)*Q[5][1]*pow(Q[7][3]-L_1, 3)*Q[5][3]*Q[7][1];
}